import Facebook from "./component/Facebook";
import React, { Component } from "react";
import facebook from '../src/image/facebook1.jpg'
import insta from '../src/image/insta.jpg'
import tic from '../src/image/tictoc.png'
import snap from '../src/image/snap.webp'
class App extends Component {
  render(){
      const data =[
    {
    name : 'facebook',
    image: facebook,
    description: 'Rejoignez des groupes, exprimez-vous, retrouvez vos amis partout dans le monde sur FB.',
    footer:'Last updated 3 mins ago',
    count:0
  },
    {
    name : 'instagram',
    image: insta,
    description: 'Rejoignez des groupes, exprimez-vous, retrouvez vos amis partout dans le monde sur FB.',
    footer:'Last updated 3 mins ago',
    count:0
  },
    {
    name : 'tic toc',
    image: tic,
    description: 'Rejoignez des groupes, exprimez-vous, retrouvez vos amis partout dans le monde sur FB.',
    footer:'Last updated 3 mins ago',
    count:0
  },
    {
    name : 'snapchat',
    image: snap,
    description: 'Rejoignez des groupes, exprimez-vous, retrouvez vos amis partout dans le monde sur FB.',
    footer:'Last updated 3 mins ago',
    count:0
  },
]
const   handleClik = ()=>{
  this.setState({
      count :this.state.count + 1
  })
  }
  return (
    <div className="App">
     <h1 className="text-center">Popular Socials Medias</h1>
      {
        data.map((element)=>{
          return <Facebook name={element.name} image={element.image} count={element.count} handleClik={()=>this.handleClik}/>
        })
      }
    </div>
  );
}

  }

export default App;
